/**
 * 数据库实体命名空间
 * 所有的数据库实体数据类型都源于这里
 */
declare namespace EN {
    /**
     * 基类实体
     */
    export interface IBaseE {
        /** id */
        id: number;
        /** 数据创建时间 */
        createTime: number;
        /** 逻辑是否删除 */
        delete: boolean;
    }

    /**
     * 测试表实体
     */
    export interface ITestE extends IBaseE {
        number: number;
        string: string;
        isActive: boolean;
        cs: string;
        cs2: string;
    }

    /**
     * 用户表
     */
    export interface IUserE extends IBaseE {
        /** 账户 */
        account: string;
        /** 密码 */
        password: string;
        /** 明文密码 */
        ewPassword: string;
        /** 登录token */
        token: string;
        /** 用户数据 */
        dataE?: IUserDataE,
    }

    /**
     * 用户数据表
     */
    export interface IUserDataE extends IBaseE {
        /** 名字 */
        name: string;
        /** 用户id */
        userId: number;
        /** 状态 */
        state: string;
        /** 个人简介 */
        introduction: string;
        /** 主题数据 */
        themeData: string;
        /** 社交数据 */
        socialData: string;
        /** 便签数据 */
        memoData: string;
    }

    /**
     * 博客表
     */
    export interface IBlogE extends IBaseE {
        /** 标题 */
        title: string;
        /** 内容 */
        content: string;
        /** 涉及到的主题 */
        theme: string;
        /** 包含的图片列表 */
        imgs: string;
        /** 涉及到的标签id列表 */
        tabIds: string;
        /** 标签实体数据 */
        tabsE?: IBlogTabE[];
        /** 外链 */
        externalLink: string;
        /** 用户id */
        userId: number;
        /** 用户数据 */
        userE?: IUserE;
        /** 显示数量 */
        showNumber: number;
    }

    /**
     * 博客标签表
     */
    export interface IBlogTabE extends IBaseE {
        /** 名字 */
        name: string;
        /** 颜色 */
        color: string;
    }

    /**
     * 便签表
     */
    export interface IMemoE extends IBaseE {
        /** x轴位置 */
        x: number;
        /** y轴位置 */
        y: number;
        /** 标题 */
        title: string;
        /** 内容 */
        content: string;
        /** 主题 */
        theme: string;
        /** 用户id */
        userId: number;
        /** 用户数据 */
        userE?: IUserE;
    }

    /**
     * 日记表
     */
    export interface IDiaryE extends IBaseE {
        /** 标题 */
        title: string;
        /** 内容 */
        content: string;
        /** 主题 */
        theme: string;
        /** 是否公开 */
        ifPublic: boolean;
        /** 用户id */
        userId: number;
        /** 用户数据 */
        userE?: IUserE;
        /** 日记补充内容 */
        supplement?: IDiarySupplementE[];
    }

    /** 日记补充表 */
    export interface IDiarySupplementE extends IBaseE {
        /** 日记id */
        diaryId: number;
        /** 内容 */
        content: string;
        /** 主题 */
        theme: string;
    }

    /**
     * 弹幕表
     */
    export interface IBulletCommentE extends IBaseE {
        /** 用户id */
        userId: number;
        /** 内容 */
        content: string;
        /** 颜色 */
        color: string;
    }

    /**
     * 博客评论
     */
    export interface IBlogComment extends IBaseE {
        /** 对应的博客id */
        blogId: number;
        /** 内容 */
        content: string;
        /** 名字 */
        name: string;
        /** 头像 */
        img: string;
        /** 主题 */
        theme: string;
        /** 标记 */
        mark: boolean;
    }

    /**
     * 贡献类型
     * 分别是博客，便签，日记，弹幕
     */
    export type ContributionsEType = 'blog' | 'memo' | 'diary' | 'bullet';
    /**
     * 贡献操作类型
     * 分别是新增和修改
     */
    export type ContributionsEConType = 'add' | 'edit';

    /**
     * 贡献
     */
    export interface IContributionsE extends IBaseE {
        /** 年 */
        y: number;
        /** 月 */
        m: number;
        /** 日 */
        d: number;
        /** 类型 */
        type: ContributionsEType;
        /** 操作类型 */
        conType: ContributionsEConType
        /** 贡献的目标id */
        targetId: number;
        /** 做出贡献的用户id */
        userId: number;
        /** 目标数据 */
        data: any;
    }
}